UPDATE active SET
  about = '<p>Jennifer''s Closet is a volunteer project being housed at Bethany Presbyterian Church. It was established by a neighborhood mom, named Jennifer, in 2023. We offer gently used clothing in sizes infant to 3X, free of charge for families in need. We are located in the church at 3000 Dewey Ave. (just north of Stone Rd.)<br><strong>We do not serve thrifters or resellers.</strong></p>',
  donate = '<p>We are now accepting clothing donations. We ask that all clothing donations are clean, unstained, and without rips.<br>If you would like to make a financial contribution to support our project, please make the checks payable to Bethany Presbyterian Church with "Clothing Closet" on the memo, 3000 Dewey Ave., Rochester, NY 14616.</p>',
  volunteer = 'If you are looking to help out in the clothing closet, we are looking for people to help sort donations, replenish clothing racks, and more. <br/> <b>Please email <a href=''mailto:lwclement68@gmail.com?subject=Donation Appointment''>lwclement68@gmail.com</a> to find a time to volunteer. </b>',
  hours = '<strong><span id="hours">We are open Tuesdays from 12:30 PM to 3 PM and Thursdays from 9:30 AM to 12 PM <br>Additionally we are open the third Saturday of each month from 10 AM to 1 PM</span></strong>'
WHERE id = 1;

